def combinationSum4(nums, target):
    # DP array: dp[i] = number of ways to make sum i
    dp = [0] * (target + 1)
    dp[0] = 1  # One way to make sum 0: use no numbers

    # For each target sum from 1 to target
    for curr_sum in range(1, target + 1):
        # Try each number in nums
        for num in nums:
            if num <= curr_sum:
                dp[curr_sum] += dp[curr_sum - num]

    return dp[target]
