def change(amount, coins):
    # dp[i] = number of ways to make amount i
    dp = [0] * (amount + 1)
    dp[0] = 1  # One way to make amount 0: use no coins

    # Process each coin
    for coin in coins:
        # Update dp array for this coin
        for i in range(coin, amount + 1):
            dp[i] += dp[i - coin]

    return dp[amount]
