class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next

    def __repr__(self):
        return f'ListNode({self.val})'

def prepare(test_case_input):
    """Convert array representation to ListNode structure"""
    def build_list(arr):
        if not arr:
            return None
        head = ListNode(arr[0])
        current = head
        for val in arr[1:]:
            current.next = ListNode(val)
            current = current.next
        return head

    l1 = build_list(test_case_input['l1'])
    l2 = build_list(test_case_input['l2'])
    return (l1, l2)

