def verify(actual_output, expected_output):
    """Verify the sum linked list result"""
    def list_to_array(node):
        result = []
        while node:
            result.append(node.val)
            node = node.next
        return result

    def list_to_string(lst):
        if not lst:
            return '[]'
        return '[' + ', '.join(str(x) for x in lst) + ']'

    actual_array = list_to_array(actual_output)
    passed = actual_array == expected_output
    output_str = list_to_string(actual_array)

    return [passed, output_str]
