def calculateBill(items):
    prices = {
        'Espresso': 3,
        'Latte': 5,
        'Croissant': 6
    }
    subtotal = sum(prices[item] for item in items)
    total_with_tax = subtotal * 1.15
    return round(total_with_tax)

