def prepare(test_case_input):
    """Prepare arguments for concatenationOfArray function"""
    return (test_case_input['nums'],)

verify:
def verify(actual_output, expected_output):
    """Verify the concatenation result"""
    def list_to_string(lst):
        if not lst:
            return '[]'
        return '[' + ', '.join(str(x) for x in lst) + ']'

    passed = actual_output == expected_output
    output_str = list_to_string(actual_output)

    return [passed, output_str]
