def canFinish(numCourses, prerequisites):
    # Build adjacency list: course -> list of prerequisites
    preMap = {i: [] for i in range(numCourses)}
    for crs, pre in prerequisites:
        preMap[crs].append(pre)

    visiting = set()

    def dfs(crs):
        # Cycle detected
        if crs in visiting:
            return False
        # Course can be completed
        if preMap[crs] == []:
            return True

        visiting.add(crs)
        for pre in preMap[crs]:
            if not dfs(pre):
                return False
        visiting.remove(crs)

        # Mark as completable
        preMap[crs] = []
        return True

    for c in range(numCourses):
        if not dfs(c):
            return False

    return True

