def verify(actual_output, expected_output):
    """Verify the course schedule result"""
    def bool_to_string(val):
        return 'true' if val else 'false'

    # Convert string 'true'/'false' to Python bool if needed
    if isinstance(expected_output, str):
        expected_output = expected_output.lower() == 'true'

    passed = actual_output == expected_output
    output_str = bool_to_string(actual_output)

    return [passed, output_str]
