def evaluateReversePolishNotation(tokens):
    stack = []
    operators = {'+', '-', '*', '/'}

    for token in tokens:
        if token in operators:
            # Pop two operands (order matters!)
            b = stack.pop()  # Second operand
            a = stack.pop()  # First operand

            # Perform operation
            if token == '+':
                result = a + b
            elif token == '-':
                result = a - b
            elif token == '*':
                result = a * b
            elif token == '/':
                # Integer division truncating toward zero
                result = int(a / b)

            stack.append(result)
        else:
            # Token is a number, push to stack
            stack.append(int(token))

    # Final result is the only element left in stack
    return stack[0]
