import heapq

class MedianFinder:
    def __init__(self):
        # Max heap for smaller half (negate values for max heap behavior)
        self.max_heap = []
        # Min heap for larger half
        self.min_heap = []

    def addNum(self, num):
        # Add to max heap first (smaller half)
        heapq.heappush(self.max_heap, -num)

        # Move largest from max heap to min heap
        heapq.heappush(self.min_heap, -heapq.heappop(self.max_heap))

        # Balance heaps: max heap should have equal or one more element
        if len(self.max_heap) < len(self.min_heap):
            heapq.heappush(self.max_heap, -heapq.heappop(self.min_heap))

    def findMedian(self):
        if len(self.max_heap) > len(self.min_heap):
            # Odd total count: median is top of max heap
            return float(-self.max_heap[0])
        else:
            # Even total count: median is average of both tops
            return (-self.max_heap[0] + self.min_heap[0]) / 2.0

def runMedianFinder(operations, values):
    """Execute MedianFinder operations and return results"""
    obj = None
    results = []

    for i, (op, val) in enumerate(zip(operations, values)):
        if op == "MedianFinder":
            obj = MedianFinder()
            results.append(None)
        elif op == "addNum":
            obj.addNum(val[0])
            results.append(None)
        elif op == "findMedian":
            results.append(obj.findMedian())

    return results

