def verify(actual_output, expected_output):
    """Verify the MedianFinder operations result"""
    def list_to_string(lst):
        if not lst:
            return '[]'
        return '[' + ', '.join(str(x) if x is not None else 'null' for x in lst) + ']'

    # Compare outputs with small tolerance for floating point
    passed = True
    if len(actual_output) != len(expected_output):
        passed = False
    else:
        for i, (actual, expected) in enumerate(zip(actual_output, expected_output)):
            if expected is None:
                if actual is not None:
                    passed = False
                    break
            elif isinstance(expected, float):
                if not isinstance(actual, (int, float)) or abs(actual - expected) > 1e-5:
                    passed = False
                    break
            else:
                if actual != expected:
                    passed = False
                    break

    output_str = list_to_string(actual_output)

    return [passed, output_str]
