def findJudge(n, trust):
    if n == 1:
        return 1

    # Track net trust: +1 when trusted, -1 when trusting
    trust_count = [0] * (n + 1)

    for a, b in trust:
        trust_count[a] -= 1  # a trusts someone (out-degree)
        trust_count[b] += 1  # b is trusted (in-degree)

    for i in range(1, n + 1):
        if trust_count[i] == n - 1:
            return i

    return -1

