def mergeStringsAlternately(word1, word2):
    result = []
    i = 0
    max_len = max(len(word1), len(word2))

    while i < max_len:
        if i < len(word1):
            result.append(word1[i])
        if i < len(word2):
            result.append(word2[i])
        i += 1

    return ''.join(result)

