def findRepeatedDnaSequences(s):
    cnt = {}
    ans = []
    for i in range(len(s) - 9):
        t = s[i:i + 10]
        cnt[t] = cnt.get(t, 0) + 1
        if cnt[t] == 2:
            ans.append(t)
    return ans

