def getSum(a, b):
    # Mask to get 32-bit integers
    mask = 0xFFFFFFFF

    while b != 0:
        # Calculate sum without carry and carry
        tmp = (a ^ b) & mask
        carry = ((a & b) << 1) & mask
        a = tmp
        b = carry

    # Handle negative numbers (if a > 0x7FFFFFFF, it's negative in 32-bit)
    if a > 0x7FFFFFFF:
        return ~(a ^ mask)
    return a

