class Solution {
    public List<Integer> binaryTreePostorderTraversal(TreeNode root) {
        List<Integer> result = new ArrayList<>();
        postorder(root, result);
        return result;
    }

    private void postorder(TreeNode node, List<Integer> result) {
        if (node == null) return;

        postorder(node.left, result);   // Traverse left subtree
        postorder(node.right, result);  // Traverse right subtree
        result.add(node.val);           // Process current node
    }
}
