function carPooling(trips, capacity) {
    // Create events: [location, passenger_change]
    const events = [];

    for (const [passengers, start, end] of trips) {
        events.push([start, passengers]);    // pickup: add passengers
        events.push([end, -passengers]);     // dropoff: remove passengers
    }

    // Sort events by location
    events.sort((a, b) => a[0] - b[0]);

    // Process events and track current passenger count
    let currentPassengers = 0;

    for (const [location, change] of events) {
        currentPassengers += change;
        if (currentPassengers > capacity) {
            return false;
        }
    }

    return true;
}

