import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

class Solution {
    public boolean carPooling(int[][] trips, int capacity) {
        // Create events: [location, passenger_change]
        List<int[]> events = new ArrayList<>();

        for (int[] trip : trips) {
            int passengers = trip[0], start = trip[1], end = trip[2];
            events.add(new int[]{start, passengers});    // pickup: add passengers
            events.add(new int[]{end, -passengers});     // dropoff: remove passengers
        }

        // Sort events by location
        Collections.sort(events, (a, b) -> Integer.compare(a[0], b[0]));

        // Process events and track current passenger count
        int currentPassengers = 0;

        for (int[] event : events) {
            int location = event[0], change = event[1];
            currentPassengers += change;
            if (currentPassengers > capacity) {
                return false;
            }
        }

        return true;
    }
}
