def gasStation(gas, cost):
    # Check if solution is possible
    if sum(gas) < sum(cost):
        return -1

    start = 0
    tank = 0

    for i in range(len(gas)):
        tank += gas[i] - cost[i]

        # If tank goes negative, reset start point
        if tank < 0:
            start = i + 1
            tank = 0

    return start

