    const totalCost = cost.reduce((sum, c) => sum + c, 0);

    if (totalGas < totalCost) {
        return -1;
    }

    let start = 0;
    let tank = 0;

    for (let i = 0; i < gas.length; i++) {
        tank += gas[i] - cost[i];

        // If tank goes negative, reset start point
        if (tank < 0) {
            start = i + 1;
            tank = 0;
        }
    }

    return start;
}

