def verify(actual_output, expected_output):
    """Verify the accounts merge result"""
    def list_to_string(lst):
        if lst is None:
            return 'None'
        if not lst:
            return '[]'
        result = []
        for item in lst:
            if isinstance(item, list):
                result.append('[' + ', '.join(repr(x) for x in item) + ']')
            else:
                result.append(repr(item))
        return '[' + ', '.join(result) + ']'

    # Handle None or invalid output
    if actual_output is None:
        return [False, 'None']

    if not isinstance(actual_output, list):
        return [False, str(actual_output)]

    # Sort each account's emails and convert to tuple for comparison
    def normalize_accounts(accounts):
        normalized = []
        for account in accounts:
            if not account or len(account) < 1:
                continue
            name = account[0]
            emails = sorted(account[1:])
            normalized.append(tuple([name] + emails))
        return set(normalized)

    actual_normalized = normalize_accounts(actual_output)
    expected_normalized = normalize_accounts(expected_output)

    passed = actual_normalized == expected_normalized
    output_str = list_to_string(actual_output)

    return [passed, output_str]
