def verify(actual_output, expected_output):
    # Sort each group and then sort the list of groups for comparison
    def normalize(groups):
        return sorted([sorted(group) for group in groups])

    actual_normalized = normalize(actual_output)
    expected_normalized = normalize(expected_output)

    passed = actual_normalized == expected_normalized

    # Format output string
    output_str = '[' + ', '.join('[' + ', '.join('"' + s + '"' for s in group) + ']' for group in actual_output) + ']'

    return [passed, output_str]
