def binaryTreePostorderTraversal(root):
    result = []

    def postorder(node):
        if not node:
            return

        postorder(node.left)   # Traverse left subtree
        postorder(node.right)  # Traverse right subtree
        result.append(node.val)  # Process current node

    postorder(root)
    return result

