python.solution_code:
def hammingWeight(n):
    count = 0
    while n:
        count += 1
        n &= n - 1  # Remove rightmost set bit
    return count

javascript.solution_code:
function hammingWeight(n) {
    let count = 0;
    while (n !== 0) {
        count++;
        n &= n - 1; // Remove rightmost set bit
    }
    return count;
}

