def bestTimeToBuyAndSellStockIi(prices):
    if not prices or len(prices) < 2:
        return 0

    max_profit = 0

    # Add profit for every price increase
    for i in range(1, len(prices)):
        if prices[i] > prices[i - 1]:
            max_profit += prices[i] - prices[i - 1]

    return max_profit
