def executeQueueOperations(operations, values):
    results = []
    queue = None
    for i, (op, val) in enumerate(zip(operations, values)):
        if op == 'Queue':
            queue = Queue()
            results.append(None)
        elif op == 'enqueue':
            results.append(queue.enqueue(val[0]))
        elif op == 'dequeue':
            results.append(queue.dequeue())
        elif op == 'front':
            results.append(queue.front())
        elif op == 'empty':
            results.append(queue.empty())
    return results

