def minCostClimbingStairs(cost):
    if not cost:
        return 0
    if len(cost) <= 2:
        return min(cost)

    # We can start from step 0 or step 1
    prev2 = cost[0]
    prev1 = cost[1]

    # Calculate min cost to reach each step
    for i in range(2, len(cost)):
        current = cost[i] + min(prev1, prev2)
        prev2 = prev1
        prev1 = current

    # We can reach the top from either of the last two steps
    return min(prev1, prev2)

