def verify(actual_output, expected_output, args=None):
    """Verify the remove element result - checks both return value and in-place modification"""
    k = actual_output

    # Check if k matches expected output
    if k != expected_output:
        return [False, f"Expected k={expected_output}, got k={k}"]

    # Check in-place modification if args provided
    if args is not None:
        nums, val = args  # Extract modified nums array and val from args tuple

        # Check that first k elements don't contain val (verifies in-place modification)
        for i in range(k):
            if nums[i] == val:
                return [False, f"Found val={val} at index {i} in first {k} elements. Array not modified correctly."]

        # Success - show k and first k elements
        if k > 0:
            result_str = f"k={k}, nums[:{k}]={nums[:k]}"
        else:
            result_str = f"k={k}"
    else:
        result_str = str(k)

    return [True, result_str]
